mapper.select:
select

 from bs_order
 where delete_flag = "0"

 and  user_name = #{userName,jdbcType=VARCHAR}


 and  user_phone = #{userPhone,jdbcType=VARCHAR}


 and product_name = #{productName,jdbcType=VARCHAR}


 and user_id = #{userId,jdbcType=INTEGER}


 and tenant_id = #{tenantId,jdbcType=VARCHAR}
